class Quiz{constructor(t){this.currentQuestionIndex=0,this.score=0,this.questions=t}getCurrentQuestion(){return this.questions[this.currentQuestionIndex]}isStart(){return 0==this.currentQuestionIndex}isEnded(){return this.questions.length==this.currentQuestionIndex+1}select(t,e){this.questions.map(s=>{s.id==t&&(s.selectedOptIndex=e,console.log(this.questions))})}next(){this.currentQuestionIndex++}previous(){this.currentQuestionIndex&&this.currentQuestionIndex--}}